import React from 'react';
import { useQuery } from 'react-query';
import axios from 'axios';
import { Container, Heading, Stack, Flex, Text, Spinner, Grid, useToast } from '@chakra-ui/react';

const Home = () => {
    const toast = useToast();
    const fetchPosts = async () => {
        try {
            const { data } = await axios.get("https://gorest.co.in/public/v2/posts");
            return data;
        } catch (error) {
            throw Error('Could not fetch posts');
        }
    }

    const { data, isLoading } = useQuery('posts', fetchPosts, {
        onError: () => {
            toast({status: error, title: error, message: error.message}    )
        }});

    if (isLoading) {
        // Handle loading state
        return (<Grid placeItems='center' height="100vh">
            <Spinner
                thickness='4px'
                speed='0.65s'
                emptyColor='gray.200'
                color='blue.500'
                size='xl'
                />
            Loading...</Grid>);
    }

    if (error) {
        // Handle error state
        return <div>Error: {error.message}</div>;
    }

    return (
        <Container maxW="1300px" mt="4">
            {data.map((post) => (
                <Stack p="4"
                    boxShadow="md"
                    borderRadius="xl"
                    border="1px solid #ccc"
                    mb="4"
                    key={post.id}>
                    <Flex justify="space-between">
                        <Text>user id: {post.user_id}</Text>
                        <Text>Post id: {post.id}</Text>
                    </Flex>
                    <Heading fontSize="2xl">{post.title}</Heading>
                    <Text>{post.body}</Text>
                </Stack>
            ))}
        </Container>
    )
}

export default Home;
