import React, { useState } from 'react';
import axios from 'axios';
import { Heading, Stack } from '@chakra-ui/react';
import { Formik, Form } from 'formik';
import { InputControl, SubmitButton, TextareaControl } from 'formik-chakra-ui';
import { useMutation, useQueryClient } from 'react-query';
import { addNewPost, updatePost } from '../../api';

const AddNewPost = ({ isUpdate, id }) => {
    const cache = useQueryClient();
    const [optimisticData, setOptimisticData] = useState(null);

    const { isLoading, error, mutateAsync } = useMutation(
        isUpdate ? 'updatePost' : 'addNewPost',
        isUpdate ? updatePost : addNewPost,
        {
            onSuccess: function () {
                console.log('inside onSuccess addingNewPost');
                // Invalidate the "posts" query to trigger a refetch
                isUpdate ? cache.invalidateQueries(['post', id]) : cache.invalidateQueries('posts');
            },
        }
    );

    const handleFormSubmit = async (values) => {
        if (isUpdate) {
            // Optimistic update: Update UI immediately
            setOptimisticData({ title: values.title, body: values.body });
        }

        try {
            // Send the mutation request
            isUpdate
                ? await mutateAsync({ title: values.title, body: values.body, id })
                : await mutateAsync({ title: values.title, body: values.body });
            console.log('values on adding new post ', values);
        } catch (error) {
            // Handle errors here
            console.error(error);

            // If the mutation fails, revert the UI to the original state
            if (isUpdate) {
                setOptimisticData(null);
            }
        }
    };

    return (
        <div>
            <Formik
                initialValues={{ title: '', body: '' }}
                onSubmit={handleFormSubmit}
            >
                <Form>
                    <Stack my="4">
                        <Heading textAlign="center">
                            {isUpdate ? 'Update Post' : 'Add New Post'}
                        </Heading>
                        <InputControl name="title" label="title" />
                        <TextareaControl name="body" label="content" />
                        <SubmitButton>
                            {isUpdate ? 'Update Post' : 'Add Post'}
                        </SubmitButton>
                    </Stack>
                </Form>
            </Formik>

            {/* Display optimistic update */}
            {optimisticData && (
                <div>
                    <Heading>Optimistic Update:</Heading>
                    <p>Title: {optimisticData.title}</p>
                    <p>Body: {optimisticData.body}</p>
                </div>
            )}
        </div>
    );
};

export default AddNewPost;
